import 'dart:math';import 'package:flutter/material.dart';import 'package:flutter_lagou_demo/test/inherited_widget_test.dart';import 'package:flutter_lagou_demo/test/random_name.dart';import 'package:flutter_lagou_demo/test/test_other.dart';import 'package:flutter_lagou_demo/test/welcome.dart';/// 测试随机名字游戏组件class NameGame extends StatefulWidget {  NameGame({Key key}) : super(key: key);  @override  _NameGameState createState() => _NameGameState();}/// 随机名字游戏组件状态管理类class _NameGameState extends State<NameGame> {  /// name 状态  String name;  /// 构造函数参数，避免父组件状态变化，而引起的子组件的重 build 操作  Widget child;  /// 构造函数  _NameGameState() {    child = Column(      children: [        Welcome(),        RandomName(),        TestOther(),      ],    );  }  @override  void initState() {    setState(() {      name = 'test flutter';    });    super.initState();  }  @override  Widget build(BuildContext context) {    return Column(      children: [        NameInheritedWidget(child: child, name: name, onNameChange: changeName),      ],    );  }  /// 修改当前名字  void changeName() {    List<String> nameList = ['flutter one', 'flutter two', 'flutter three'];    int pos = Random().nextInt(3);    setState(() {      name = nameList[pos];    });  }}