import 'package:flutter/material.dart';import 'package:provider/provider.dart';import '../model/name_model.dart';import '../widgets/index.dart';class ProviderNameGame extends StatelessWidget {  final name = NameModel();  @override  Widget build(BuildContext context) {    return Column(      children: [        Provider<String>.value(          child: ChangeNotifierProvider.value(            value: name,            child: Column(              children: [                ProviderWelcome(),                ProviderRandomName(),              ],            ),          ),        ),        ProviderTestOther(),      ],    );  }}