import 'package:flutter/material.dart';import 'package:provider/provider.dart';import '../model/name_model.dart';class ProviderRandomName extends StatelessWidget {  @override  Widget build(BuildContext context) {    print('provider random name build.');    final _name = Provider.of<NameModel>(context);        return FlatButton(      child: Text(_name.value),      onPressed: () => _name.changeName(),    );  }}