import 'package:flutter/material.dart';import 'inherited_widget_test.dart';/// 随机展示人名组件class RandomName extends StatelessWidget {  @override  Widget build(BuildContext context) {    final String name =        context.dependOnInheritedWidgetOfExactType<NameInheritedWidget>(aspect: NameInheritedWidget).name;    final Function onChangeName =        context.dependOnInheritedWidgetOfExactType<NameInheritedWidget>(aspect: NameInheritedWidget).onNameChange;    return FlatButton(      child: Text(name),      onPressed: () => onChangeName(),    );  }}