import 'dart:ffi';import 'package:flutter/material.dart';import 'package:flutter_lagou_demo/test/redux/states/name_states.dart';import 'package:flutter_redux/flutter_redux.dart';import 'package:redux/redux.dart';class ReduxRandomName extends StatelessWidget {  final Store store;  ReduxRandomName({Key key, this.store}) : super(key: key);  @override  Widget build(BuildContext context) {    print('redux random name build.');    return StoreConnector<NameStates, String>(      converter: (store) => store.state.name.toString(),      builder: (context, name) {        return StoreConnector<NameStates, VoidCallback>(          converter: (store) {            return () => store.dispatch(NameActions.changeName);          },          builder: (context, callback) {            return FlatButton(              child: Text(name),              onPressed: () => callback(),            );          },        );      },    );  }}