import 'package:flutter/material.dart';import 'package:flutter_lagou_demo/styles/text_styles.dart';import '../models/comment_info.dart';class ArticleDetailComments extends StatelessWidget {  /// 传入的帖子评论信息  final List<CommentInfo> commentList;  const ArticleDetailComments({Key key, this.commentList}) : super(key: key);  /// 获取单行的评论展示信息  ///  /// 包括展示评论内容，以及评论者头像和昵称  Widget getOneItemSection(CommentInfo commentItem) {    return Row(      mainAxisAlignment: MainAxisAlignment.start,      children: [        Expanded(          flex: 2,          child: ClipRRect(            borderRadius: BorderRadius.circular(6),            child: Image.network(              commentItem.userInfo.avatar,              width: 50.0,              height: 50.0,              fit: BoxFit.scaleDown,            ),          ),        ),        Expanded(          flex: 6,          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Text(                commentItem.userInfo.nickname,                style: TextStyles.commonStyle(),              ),              Text(                commentItem.comment,                style: TextStyles.commonStyle(.8),              )            ],          ),        )      ],    );  }  @override  Widget build(BuildContext context) {    return ListView.separated(      scrollDirection: Axis.vertical,      shrinkWrap: true,      itemCount: commentList.length,      itemBuilder: (context, position) {        return getOneItemSection(commentList[position]);      },      separatorBuilder: (context, index) {        return Divider(          height: .5,          indent: 75,          color: Color(0xFFDDDDDD),        );      },    );  }}