import 'package:flutter/material.dart';import '../models/article_info.dart';import '../models/user_info.dart';import 'article_summary.dart';import 'article_bottom_bar.dart';import 'article_like_bar.dart';import 'package:flutter_lagou_demo/pages/article_detail.dart';/// 此为帖子描述类，包括了帖子UI中的所有元素class ArticleCard extends StatelessWidget {  /// 传入的用户信息  final UserInfo userInfo;  /// 传入的帖子信息  final ArticleInfo articleInfo;  const ArticleCard({Key key, this.userInfo, this.articleInfo}) : super(key: key);  /// 执行页面跳转到article_detail  void goToArticleDetailPage(BuildContext context) {    Navigator.push(context, MaterialPageRoute(builder: (context) {      return ArticleDetail();    }));  }  @override  Widget build(BuildContext context) {    return Container(      color: Colors.white,      padding: EdgeInsets.all(8),      child: FlatButton(        onPressed: () => goToArticleDetailPage(context),        child: Column(          children: [            ArticleSummary(              title: articleInfo.title,              summary: articleInfo.summary,              articleImage: articleInfo.articleImage,            ),            Row(              children: [                Expanded(                  flex: 9,                  child: ArticleBottomBar(                    nickname: userInfo.nickname,                    avatar: userInfo.avatar,                    commentNum: articleInfo.commentNum,                  ),                ),                Expanded(                  flex: 3,                  child: ArticleLikeBar(),                )              ],            )          ],        ),      ),    );  }}