import 'package:flutter/material.dart';import '../models/article_info.dart';import '../models/user_info.dart';import 'article_summary.dart';import 'article_bottom_bar.dart';import 'article_like_bar.dart';/// 此为帖子描述类，包括了帖子UI中的所有元素class ArticleCard extends StatelessWidget {  /// 传入的用户信息  final UserInfo userInfo;  /// 传入的帖子信息  final ArticleInfo articleInfo;  const ArticleCard({Key key, this.userInfo, this.articleInfo}) : super(key: key);  @override  Widget build(BuildContext context) {    return Column(      children: [        ArticleSummary(          title: articleInfo.title,          summary: articleInfo.summary,          articleImage: articleInfo.articleImage,        ),        Row(          children: [            ArticleBottomBar(              nickname: userInfo.nickname,              avatar: userInfo.avatar,              commentNum: articleInfo.commentNum,            ),            ArticleLikeBar(likeNum: articleInfo.likeNum)          ],        )      ],    );  }}