import 'package:flutter/material.dart';import 'package:flutter_test/flutter_test.dart';import 'package:image_test_utils/image_test_utils.dart';import 'package:flutter_lagou_demo/widgets/home_page/article_bottom_bar.dart';void main() {  testWidgets('test article bottom bar', (WidgetTester tester) async {    // 因为涉及图片组件，为了避免图片组件在测试加载过程中的异常问题    provideMockedNetworkImages(() async {      final Widget testWidgets = ArticleBottomBar(        nickname: 'test',        avatar: 'https://i.pinimg.com/originals/e0/64/4b/e0644bd2f13db50d0ef6a4df5a756fd9.png',        commentNum: 1,      );      await tester.pumpWidget(        new MaterialApp(          home: testWidgets,        )      );            expect(find.text('test'), findsOneWidget);      expect(find.text('1'), findsOneWidget);      expect(find.byWidget(testWidgets), findsOneWidget);    });  });}