import 'package:flutter_test/flutter_test.dart';import 'package:flutter_lagou_demo/widgets/models/article_info.dart';void main() {  final ArticleInfo articleInfo = ArticleInfo('test', 'test summary', 'https://test.com', 10, 5);    test('test articleinfo', () {    expect(articleInfo.title == 'test', true);    expect(articleInfo.summary == 'test summary', true);    expect(articleInfo.articleImage == 'https://test.com', true);    expect(articleInfo.commentNum == 10, true);    expect(articleInfo.likeNum == 5, true);  });}