import 'package:flutter_test/flutter_test.dart';import 'package:flutter_lagou_demo/widgets/models/comment_info.dart';import 'package:flutter_lagou_demo/widgets/models/user_info.dart';void main() {  final UserInfo userInfo = UserInfo('test', 'http://test.com');  test('test-userinfo', () {    final CommentInfo commentInfo = CommentInfo(userInfo, 'Comment test');        expect(commentInfo.comment == 'Comment test', true);    expect(commentInfo.userInfo.nickname == 'test', true);    expect(commentInfo.userInfo.avatar == 'http://test.com', true);  });}