import '../index.dart';import 'package:flukit/flukit.dart';class HomeRoute extends StatefulWidget {  @override  _HomeRouteState createState() => _HomeRouteState();}class _HomeRouteState extends State<HomeRoute> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text(GmLocalizations.of(context).home),      ),//      body: ,    );  }  Widget _buildBody() {    UserModel userModel = Provider.of(context);    if (!userModel.isLogin) {      // 用户未登陆，显示登陆按钮      return Center(        child: RaisedButton(          child: Text(GmLocalizations.of(context).login),          onPressed: () => Navigator.of(context).pushNamed('login'),        ),      );    } else {      // 已登录，则显示项目列表      return InfiniteListView(        onRetrieveData: (int page, List<Repo> items, bool refresh) async {          var data = await Git(context).getRepos(            refresh: refresh,            queryParameters: {              'page': page,              'page_size': 20,            },          );          // 把请求到的新数据添加到items中          items.addAll(data);          return data.length > 0 && data.length % 20 == 0;        },        itemBuilder: (List list, int index, BuildContext context) {          // 项目信息列表项//          return Repo        },      );    }  }}