import '../index.dart';class ThemeChangeRoute extends StatelessWidget {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text(GmLocalizations.of(context).theme),      ),      // 显示主题色块      body: ListView(        children: Global.themes.map<Widget>((e) {          return GestureDetector(            child: Padding(              padding: const EdgeInsets.symmetric(vertical: 5, horizontal: 16),              child: Container(                color: e,                height: 40,              ),            ),            onTap: () => Provider.of<ThemeModel>(context).theme = e, // 主题更新后，MaterialApp会重新build          );        }).toList(),      ),    );  }}