import '../index.dart';class RepoItem extends StatefulWidget {  RepoItem(this.repo) : super(key: ValueKey(repo.id));  final Repo repo;  @override  _RepoItemState createState() => _RepoItemState();}class _RepoItemState extends State<RepoItem> {  @override  Widget build(BuildContext context) {    var subtitle;    return Padding(      padding: const EdgeInsets.only(top: 8.0),      child: Material(        color: Colors.white,        shape: BorderDirectional(          bottom: BorderSide(            color: Theme.of(context).dividerColor,            width: .5,          ),        ),        child: Padding(          padding: const EdgeInsets.only(top: 0.0, bottom: 16.0),          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: <Widget>[              ListTile(                dense: true,                leading: gmAvatar(                  // 项目owner头像                  widget.repo.owner.avatar_url,                  width: 24.0,                  borderRadius: BorderRadius.circular(12),                ),                title: Text(                  widget.repo.owner.login,                  textScaleFactor: .9,                ),                subtitle: subtitle,                trailing: Text(widget.repo.language ?? ''),              ),              // 构建项目标题和简介              Padding(                padding: const ,              ),            ],          ),        ),      ),    );  }}